import React from "react";
import styled from "styled-components";
import { useContextState } from "../utils/StateContext";

const StyledBox = styled.div`
  width: 100px;
  height: 100px;
  margin: 10px;
  background-color: grey;
  color: black;
  border-radius: 5px;
`;

const Box = ({ rowId, columnId }) => {
  const state = useContextState();
  const onClickHandler = () => {
    if (rowId === 0 && columnId === 0) {
      const copyOfBoard = [...state.board[0]];
      copyOfBoard[0][0] = "x";
      console.log(copyOfBoard);
    }

    // console.log("rowId: ", rowId, "columnId: ", columnId);
    // // state.board[1]();
    // console.log(state);
  };
  return <StyledBox onClick={() => onClickHandler()}></StyledBox>;
};

export default Box;
